// File inclusion\n$start = microtime(true);\ninclude 'terms.php'; // assume terms.php contains 3,000 terms\n$end = microtime(true);\necho "File inclusion: " . ($end - $start) . " seconds\n";\n\n// MySQL query\n$start = microtime(true);\n$db = new mysqli('localhost', 'username', 'password', 'database');\n$result = $db->query("SELECT term FROM terms");\n$terms = array();\nwhile ($row = $result->fetch_assoc()) {\n    $terms[] = $row['term'];\n}\n$end = microtime(true);\necho "MySQL query: " . ($end - $start) . " seconds\n";