$xmlFile = 'bugs_features_listdata.xml';\n$rootNode = 'root'; // Change this to your root node name\n\n// Load the XML file\n$xml = simplexml_load_file($xmlFile);\n\n// If the file is empty, create the root node\nif ($xml === false) {\n    $xml = new SimpleXMLElement("<$rootNode/>");\n}\n\n// Add the new node\n$newNode = simplexml_load_string($_POST['listData']);\n$xml->addChild($newNode->getName(), $newNode);\n\n// Save the XML file\n$xml->asXML($xmlFile);