class SessionHandler implements SessionHandlerInterface {\n    private $db;\n\n    public function __construct(PDO $db) {\n        $this->db = $db;\n    }\n\n    public function open($savePath, $sessionName) {\n        // Update user status to online when session opens\n        $this->db->query("UPDATE users SET is_online = 1 WHERE id = " . $_SESSION['user_id']);\n        return true;\n    }\n\n    public function close() {\n        // Update user status to offline when session closes\n        $this->db->query("UPDATE users SET is_online = 0 WHERE id = " . $_SESSION['user_id']);\n        return true;\n    }\n\n    // Implement other SessionHandlerInterface methods...\n}\n\n// Register the custom session handler\n$handler = new SessionHandler($pdo);\nsession_set_save_handler($handler);