// Assuming you're using PDO for database connection\n$stmt = $pdo->prepare("SELECT id FROM items WHERE parent_id NOT IN (SELECT id FROM items)");\n$stmt->execute();\n$orphanedIds = $stmt->fetchAll(PDO::FETCH_COLUMN);\n\nif ($orphanedIds) {\n    $placeholders = implode(',', array_fill(0, count($orphanedIds), '?'));\n    $stmt = $pdo->prepare("DELETE FROM items WHERE id IN ($placeholders)");\n    $stmt->execute($orphanedIds);\n}