class My_View_Helper_FormElementArray extends Zend_View_Helper_FormElement\n{\n    public function formElementArray($name, $value = null, $attribs = null, $options = null, $listsep = "\n")\n    {\n        $info = $this->_getInfo($name, $value, $attribs, $options, $listsep);\n        $view = $this->view;\n\n        $elementContent = '';\n        foreach ($info['options'] as $key => $val) {\n            $attribs['name'] = $name . '[' . $key . ']';\n            $elementContent .= $view->formText($name . '[' . $key . ']', $val, $attribs) . $listsep;\n        }\n\n        return $elementContent;\n    }\n}