// Create a function to recreate the $sortArr array\nfunction createSortArray($records, $fields) {\n    $sortArr = array();\n    foreach ($fields as $field) $sortArr[$field] = array();\n    foreach ($records as $r) {\n        foreach ($r as $key => $value) {\n            $sortArr[$key][] = $value;\n        }\n    }\n    return $sortArr;\n}\n\n// Before each sort, recreate the $sortArr array\n$sortArr = createSortArray($records, $fields);\narray_multisort(\n    $sortArr['Date Completed'], SORT_DESC,\n    $sortArr['Last Name'], SORT_ASC,\n    $sortArr['First Name'], SORT_ASC,\n    $sortArr['Course'], SORT_ASC,\n    $records\n);