function parseOutputFile($file_path) {\n    $blocks = [];\n    $content = file_get_contents($file_path);\n    $lines = explode("\n", $content);\n    $current_block = [];\n\n    foreach ($lines as $line) {\n        if (trim($line) === '<block') {\n            $current_block = [];\n        } elseif (trim($line) === '</block>') {\n            $blocks[] = $current_block;\n        } else {\n            list($key, $value) = explode('=', trim($line));\n            $current_block[trim($key)] = trim($value);\n        }\n    }\n\n    return $blocks;\n}