function is_jpeg_corrupted($file_path) {\n    $fp = fopen($file_path, 'rb');\n    if (!$fp) {\n        return true; // unable to open file\n    }\n\n    $header = fread($fp, 2);\n    if ($header !== "\xFF\xD8") {\n        fclose($fp);\n        return true; // not a JPEG file\n    }\n\n    fseek($fp, -2, SEEK_END);\n    $footer = fread($fp, 2);\n    fclose($fp);\n\n    if ($footer !== "\xFF\xD9") {\n        return true; // corrupted JPEG file\n    }\n\n    return false; // valid JPEG file\n}