$handle = popen('curl -o '.VIDEOPATH.$fileName.'.flv '.$url.' 2>&1', 'rb');\n\nwhile(!feof($handle)) {\n    $line = fgets($handle, 4096);\n    $line = preg_replace("/s+/", " ", $line);\n    $fields = explode(" ", $line);\n\n    // Check if the line contains the percentage\n    if (isset($fields[1]) && $fields[1] == '%') {\n        $percentage = $fields[0];\n        fwrite($fh, $percentage . "\n");\n        echo "Percentage: $percentage%\n";\n    }\n}