// settings.json\n{\n    "UPLOAD_DIR": "uploads/",\n    "DISPLAY_NUM": true,\n    "names": {\n        "a": "b",\n        "c": "d",\n        "e": "f"\n    }\n}\n\n// PHP code to read and write settings\n$settingsFile = 'settings.json';\n\n// Read settings\n$settings = json_decode(file_get_contents($settingsFile), true);\n\n// Update settings\n$settings['UPLOAD_DIR'] = 'new-upload-dir';\n$settings['names']['g'] = 'h';\n\n// Write settings\nfile_put_contents($settingsFile, json_encode($settings, JSON_PRETTY_PRINT));