class Thing {\n    function __construct(array $props=array()) {\n        foreach ($props as $key => $value) {\n            $this->{'_'.$key} = $value;\n        }\n    }\n\n    public function __get($name) {\n        return $this->{'_'.$name};\n    }\n}\n\n$foo = new Thing(array('bar' => 'baz'));\necho $foo->bar; // outputs "baz"