function getBestImage($imageArray) {\n    $bestImage = null;\n\n    foreach ($imageArray as $image) {\n        // Rule 1: Check for "-large" in the filepath\n        if (strpos($image, '-large') !== false) {\n            $bestImage = $image;\n            break;\n        }\n\n        // Get the image size\n        $size = getimagesize($image);\n        $width = $size[0];\n\n        // Rule 2: Check for 260px wide\n        if ($width == 260) {\n            $bestImage = $image;\n            break;\n        }\n\n        // Rule 3: Check for 265px wide\n        if ($width == 265) {\n            $bestImage = $image;\n            break;\n        }\n\n        // Rule 4: Check for 600px wide\n        if ($width == 600) {\n            $bestImage = $image;\n            break;\n        }\n\n        // Rule 5: Check for 220px wide\n        if ($width == 220) {\n            $bestImage = $image;\n            break;\n        }\n    }\n\n    return $bestImage;\n}\n\n// Example usage:\n$imageArray = array('path/to/image1.jpg', 'path/to/image2-large.jpg', 'path/to/image3.jpg');\n$bestImage = getBestImage($imageArray);\necho $bestImage; // Output: path/to/image2-large.jpg