function send_file_as_attachment($file) {\n    // Send the file as an attachment to an email\n    $to = 'example@example.com';\n    $subject = 'File Attachment';\n    $message = 'Please find the attached file.';\n    $headers = 'Content-Type: application/octet-stream; name="' . $file['name'] . '"';\n    $attachment = chunk_split(base64_encode(file_get_contents($file['tmp_name'])));\n\n    mail($to, $subject, $message, $headers, $attachment);\n}