// In your controller\npublic function view($uid) {\n    $this->paginate = array(\n        'Entity' => array(\n            'conditions' => array('Collection.uid' => $uid),\n            'contain' => array('Collection'),\n            'limit' => 10 // number of entities per page\n        )\n    );\n\n    $entities = $this->paginate('Entity');\n    $this->set('entities', $entities);\n}