function executeUserCode($code) {\n    // Create a new PHP process with limited privileges\n    $descriptorspec = array(\n        0 => array("pipe", "r"),  // stdin\n        1 => array("pipe", "w"),  // stdout\n        2 => array("pipe", "w")   // stderr\n    );\n    $process = proc_open('php', $descriptorspec, $pipes);\n\n    // Write the user code to the process's stdin\n    fwrite($pipes[0], $code);\n    fclose($pipes[0]);\n\n    // Read the output from the process's stdout and stderr\n    $output = stream_get_contents($pipes[1]);\n    $error = stream_get_contents($pipes[2]);\n    fclose($pipes[1]);\n    fclose($pipes[2]);\n\n    // Close the process\n    proc_close($process);\n\n    // Return the output and error messages\n    return array($output, $error);\n}\n\n// Example usage:\n$code = $_POST['codeInput'];\n$result = executeUserCode($code);\nprint_r($result);