function countSentences($text) {\n    $sentences = preg_split('/[.!?]+/', $text, -1, PREG_SPLIT_NO_EMPTY);\n    return count($sentences);\n}\n\nfunction countSyllables($word) {\n    // Use a syllable counting library or implement a basic algorithm\n    // For demonstration purposes, I'll use a simple vowel-counting approach\n    $vowels = array('a', 'e', 'i', 'o', 'u');\n    $count = 0;\n    foreach (str_split(strtolower($word)) as $char) {\n        if (in_array($char, $vowels)) {\n            $count++;\n        }\n    }\n    return $count;\n}\n\nfunction calculateReadabilityScore($text) {\n    $totalSentences = countSentences($text);\n    $totalWords = str_word_count($text);\n    $totalSyllables = 0;\n    foreach (explode(' ', $text) as $word) {\n        $totalSyllables += countSyllables($word);\n    }\n    $score = 206.835 - (1.015 * $totalWords / $totalSentences) - (84.6 * $totalSyllables / $totalWords);\n    return $score;\n}\n\nfunction readability($text) {\n    if (empty($text)) {\n        throw new InvalidArgumentException('Input text is empty');\n    }\n    return calculateReadabilityScore($text);\n}