function editBlock($block_id, $new_data) {\n    $blocks = parseOutputFile('output.php');\n    $blocks[$block_id] = $new_data;\n    writeOutputFile('output.php', $blocks);\n}\n\nfunction writeOutputFile($file_path, $blocks) {\n    $content = '';\n    foreach ($blocks as $block) {\n        $content .= '<block>' . "\n";\n        foreach ($block as $key => $value) {\n            $content .= "  $key = $value\n";\n        }\n        $content .= '</block>' . "\n";\n    }\n    file_put_contents($file_path, $content);\n}