// Define a class\nclass Person {\n    public $name;\n    public $age;\n\n    public function __construct($name, $age) {\n        $this->name = $name;\n        $this->age = $age;\n    }\n}\n\n// Create an array to store objects\n$people = array();\n\n// Create objects and add them to the array\n$person1 = new Person("John", 30);\n$people[] = $person1;\n\n$person2 = new Person("Jane", 25);\n$people[] = $person2;\n\n// Print the array\nprint_r($people);