<?php\n\n// Configuration\n$root_dir = 'path/to/root/directory';\n$allowed_extensions = array('pdf', 'doc', 'docx');\n\n// Get current directory\n$current_dir = isset($_GET['dir']) ? $_GET['dir'] : $root_dir;\n\n// Check if directory exists and is inside root directory\nif (!file_exists($current_dir) || strpos($current_dir, $root_dir) !== 0) {\n    $current_dir = $root_dir;\n}\n\n// List directories and files\n$directories = array();\n$files = array();\nforeach (scandir($current_dir) as $item) {\n    if ($item == '.' || $item == '..') {\n        continue;\n    }\n    $path = $current_dir . DIRECTORY_SEPARATOR . $item;\n    if (is_dir($path)) {\n        $directories[] = $item;\n    } elseif (is_file($path)) {\n        $extension = pathinfo($path, PATHINFO_EXTENSION);\n        if (in_array($extension, $allowed_extensions)) {\n            $files[] = $item;\n        }\n    }\n}\n\n// Output\necho '<h1>Directories:</h1>';\nforeach ($directories as $directory) {\n    echo '<p><a href="?dir=' . urlencode($current_dir . DIRECTORY_SEPARATOR . $directory) . '">' . $directory . '</a></p>';\n}\necho '<h1>Files:</h1>';\nforeach ($files as $file) {\n    echo '<p><a href="' . $current_dir . DIRECTORY_SEPARATOR . $file . '">' . $file . '</a></p>';\n}