class QueryGeneratorFactory {\n    public static function createQueryGenerator($platform) {\n        switch ($platform) {\n            case 'mysql':\n                return new MySQLQueryGenerator();\n            case 'mssql':\n                return new MSSQLQueryGenerator();\n            // Add more cases for other platforms\n            default:\n                throw new InvalidArgumentException("Unsupported platform");\n        }\n    }\n}