class Foo {\n    public $title;\n\n    public function __construct() {\n        ob_start();\n        echo "<html>\n";\n        echo "<head>\n";\n        echo "<title></title>\n";\n        echo "</head>\n";\n        echo "<body>\n";\n    }\n\n    public function setTitle($title) {\n        $this->title = $title;\n    }\n\n    public function __destruct() {\n        echo "</body>\n";\n        echo "</html>\n";\n        $output = ob_get_contents();\n        ob_end_clean();\n        $output = str_replace("<title></title>", "<title>".$this->title."</title>", $output);\n        echo $output;\n    }\n}