function getUniqueFilename($filename, $destination) {\n    $pathInfo = pathinfo($filename);\n    $extension = $pathInfo['extension'];\n    $base = $pathInfo['filename'];\n    $counter = 1;\n\n    while (file_exists($destination . '/' . $filename)) {\n        $filename = $base . '_' . $counter . '.' . $extension;\n        $counter++;\n    }\n\n    return $filename;\n}\n\n// Example usage:\n$destination = 'uploads/';\n$filename = $_FILES['file']['name'];\n$uniqueFilename = getUniqueFilename($filename, $destination);\nmove_uploaded_file($_FILES['file']['tmp_name'], $destination . '/' . $uniqueFilename);