// components/ProductWidget.php\nclass ProductWidget {\n    private $model;\n    private $view;\n\n    public function __construct(ProductModel $model, ProductView $view) {\n        $this->model = $model;\n        $this->view = $view;\n    }\n\n    public function render($params = []) {\n        $data = $this->model->getData($params);\n        return $this->view->render($data);\n    }\n}