class Foo {\n    public $title;\n\n    public function __construct() {}\n\n    public function setTitle($title) {\n        $this->title = $title;\n    }\n\n    public function render() {\n        echo "<html>\n";\n        echo "<head>\n";\n        echo "<title>".$this->title."</title>\n";\n        echo "</head>\n";\n        echo "<body>\n";\n    }\n\n    public function __destruct() {\n        echo "</body>\n";\n        echo "</html>\n";\n    }\n}