function is_gsm0338($message) {\n    $gsm0338_chars = '@£$¥èéùìòÇØøÅåΔ_ΦΓΛΩΠΨΣΘΞÆæßÉ !"#$¤%&\'()*+,-./0123456789:;<=>?¡ABCDEFGHIJKLMNOPQRSTUVWXYZÄÖÑܧ¿abcdefghijklmnopqrstuvwxyzäöñüà';\n    for ($i = 0; $i < strlen($message); $i++) {\n        if (strpos($gsm0338_chars, $message[$i]) === false) {\n            return false;\n        }\n    }\n    return true;\n}\n\nfunction convert_to_unicode($message) {\n    $unicode_message = '';\n    foreach (str_split($message) as $char) {\n        $unicode_message .= '\\u' . sprintf('%04x', ord($char));\n    }\n    return $unicode_message;\n}\n\n$message = "Let's test öäü éàè אין תמיכה בעברית";\n\nif (is_gsm0338($message)) {\n    echo "Message is GSM 03.38 compatible.\n";\n} else {\n    echo "Message is not GSM 03.38 compatible, converting to Unicode...\n";\n    $unicode_message = convert_to_unicode($message);\n    echo "Unicode message: $unicode_message\n";\n}