public static function loadConfig($file, $type)\n{\n    if (!file_exists($file)) {\n        throw new Asra_Core_Exception("{$type} file was not present at specified location: {$file}");\n    }\n\n    try {\n        $parse = parse_ini_file($file, true);\n        if (!is_array($parse)) {\n            throw new Asra_Core_Exception("{$type} file at {$file} appears to be invalid.");\n        }\n    } catch (Exception $e) {\n        throw new Asra_Core_Exception("Error parsing {$type} file at {$file}: " . $e->getMessage());\n    }\n}