private function GenerateImage()\n{\n    $original = imagecreatefrompng($this->ImagePath());\n\n    $x = imagesx($original);\n    $y = imagesy($original);\n\n    $image = imagecreatetruecolor($x, $y);\n\n    imagealphablending($image, false);\n    imagesavealpha($image, true);\n\n    // Fill the entire image with the desired color\n    $colour = imagecolorallocatealpha($image, $this->RGB[0], $this->RGB[1], $this->RGB[2], 127); // 127 is the alpha value (0-127)\n    imagefill($image, 0, 0, $colour);\n\n    // Copy the original image over the filled image, using alpha blending\n    imagecopyresampled($image, $original, 0, 0, 0, 0, $x, $y, $x, $y);\n\n    return imagepng($image, $this->GeneratedPath());\n\n    imagedestroy($original);\n    imagedestroy($image);\n}