class Settings {\n    private $settings;\n\n    public function __construct($file) {\n        $this->settings = json_decode(file_get_contents($file), true);\n    }\n\n    public function getConfig($section, $subsection, $key) {\n        return $this->settings[$section][$subsection][$key];\n    }\n\n    public function setConfig($section, $subsection, $key, $value) {\n        $this->settings[$section][$subsection][$key] = $value;\n        file_put_contents($file, json_encode($this->settings));\n    }\n}