// Import SimplePie library\nrequire_once 'autoloader.php';\n\n// Set RSS feed URL ( validate and sanitize this )\n$rss_url = 'http://jacobnlsn.com/wordpress/?feed=rss';\n\n// Initialize SimplePie\n$feed = new SimplePie();\n$feed->set_feed_url($rss_url);\n$feed->init();\n\n// Get the first X items from the feed ( e.g., 5 items )\n$items = $feed->get_items(0, 5);\n\n// Loop through items and display them\nforeach ($items as $item) {\n    echo '<h2>' . $item->get_title() . '</h2>';\n    echo '<p>' . $item->get_description() . '</p>';\n    echo '<p><a href="' . $item->get_permalink() . '">Read more</a></p>';\n}