<?php\n// Configuration\n$upload_dir = 'uploads/';\n$allowed_extensions = array('csv');\n\n// Check if file is uploaded\nif ($_FILES['file']['size'] > 0) {\n    $file_name = $_FILES['file']['name'];\n    $file_ext = pathinfo($file_name, PATHINFO_EXTENSION);\n    if (in_array($file_ext, $allowed_extensions)) {\n        $file_path = $upload_dir . $file_name;\n        move_uploaded_file($_FILES['file']['tmp_name'], $file_path);\n        $csv_data = file_get_contents($file_path);\n    } else {\n        echo 'Only CSV files are allowed.';\n        exit;\n    }\n}\n\n// Display CSV data\nif (isset($csv_data)) {\n    $lines = explode("\n", $csv_data);\n    foreach ($lines as $line) {\n        $fields = str_getcsv($line);\n        echo '<tr>';\n        foreach ($fields as $field) {\n            echo '<td><input type="text" value="' . $field . '"></td>';\n        }\n        echo '</tr>';\n    }\n}\n\n// Save CSV data\nif (isset($_POST['save'])) {\n    $csv_data = '';\n    $lines = $_POST['lines'];\n    foreach ($lines as $line) {\n        $fields = $line['fields'];\n        $csv_data .= implode(',', $fields) . "\n";\n    }\n    file_put_contents($file_path, $csv_data);\n    echo 'CSV file saved successfully.';\n}\n\n// HTML Form\n?>\n<form action="<?php echo $_SERVER['PHP_SELF']; ?>" method="post" enctype="multipart/form-data">\n    <input type="file" name="file" accept=".csv">\n    <button type="submit">Open CSV File</button>\n</form>\n\n<form action="<?php echo $_SERVER['PHP_SELF']; ?>" method="post">\n    <table>\n        <?php // Display CSV data here ?>\n    </table>\n    <button type="submit" name="save">Save CSV File</button>\n</form>