class User {\n    protected $id;\n    protected $username;\n    protected $password;\n    protected $email;\n    // ...\n\n    public static function load($id) {\n        $query = "SELECT * FROM users WHERE id = :id";\n        $stmt = PDO::prepare($query);\n        $stmt->execute([':id' => $id]);\n        $data = $stmt->fetch();\n\n        if ($data['type'] === 'agent') {\n            return new Agent($data);\n        } elseif ($data['type'] === 'lead') {\n            return new Lead($data);\n        } else {\n            return new User($data);\n        }\n    }\n\n    public function __construct($data) {\n        $this->id = $data['id'];\n        $this->username = $data['username'];\n        $this->password = $data['password'];\n        $this->email = $data['email'];\n        // ...\n    }\n}\n\nclass Agent extends User {\n    protected $agentSpecificData;\n\n    public function __construct($data) {\n        parent::__construct($data);\n        $this->agentSpecificData = $data['agent_specific_data'];\n    }\n}\n\nclass Lead extends User {\n    protected $leadSpecificData;\n\n    public function __construct($data) {\n        parent::__construct($data);\n        $this->leadSpecificData = $data['lead_specific_data'];\n    }\n}