abstract class Turnable {\n    abstract public function turn();\n}\n\nclass Screwdriver extends Turnable {\n    public function turn() {\n        // implementation for turning a screwdriver\n        echo "Turning screwdriver...\n";\n    }\n}\n\nclass SteeringWheel extends Turnable {\n    public function turn() {\n        // implementation for turning a steering wheel\n        echo "Turning steering wheel...\n";\n    }\n}\n\nfunction turn(Turnable $object) {\n    $object->turn();\n}\n\n$screwdriver = new Screwdriver();\n$steeringWheel = new SteeringWheel();\n\nturn($screwdriver); // outputs "Turning screwdriver..."\nturn($steeringWheel); // outputs "Turning steering wheel..."