$ip = $_SERVER['REMOTE_ADDR'];\n$date = date("j. F Y");\n$time = date("H:i:s");\n\n// Using a CSV file\n$file = './iplogg.csv';\n$f = fopen($file, 'a');\nfputcsv($f, array($ip, $date, $time));\nfclose($f);\n\n// Generate the HTML table on the fly\n$records = array();\nif (($handle = fopen($file, 'r')) !== FALSE) {\n    while (($data = fgetcsv($handle, 1000, ",")) !== FALSE) {\n        $records[] = $data;\n    }\n    fclose($handle);\n}\n\necho "<table cellpadding='6' rules='groups' frame='no'>";\necho "<thead><tr><th>IP</th><th>Date</th><th>Time</th></tr></thead>";\necho "<tbody>";\nforeach ($records as $record) {\n    echo "<tr><td>{$record[0]}</td><td>{$record[1]}</td><td>{$record[2]}</td></tr>";\n}\necho "</tbody></table>";