// Get URL from $_GET\n$url = $_GET['url'];\n\n// Send redirect header to client\nheader('Location: ' . $url);\nob_start();\nob_flush();\n\n// Connect to database and log visit\n$conn = mysqli_connect("localhost", "username", "password", "database");\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Create row for URL, or update with 1 hit if it exists\n$query = "INSERT INTO clicks (url, hits) VALUES ('$url', 1) ON DUPLICATE KEY UPDATE hits=hits+1";\n$conn->query($query);\n$conn->close();