<?php\nif (isset($_POST['reporte'])) {\n    $falla = $_POST['reporte'];\n} else {\n    $falla = "";\n}\n\nif (!isset($falla)) {\n    echo '<font color="red">Intentó enviar una forma vacía. Por favor intente de nuevo.</font>';\n} else {\n    $host = "localhost";\n    $username = "user";\n    $password = "pass";\n    $db_name = "cosa";\n    $tbl_name = "reportes";\n\n    // Create a MySQL connection\n    $mysqli = new mysqli($host, $username, $password, $db_name);\n\n    // Check for connection errors\n    if ($mysqli->connect_error) {\n        echo "Error: " . $mysqli->connect_error;\n        exit();\n    }\n\n    // Prepare the query\n    $stmt = $mysqli->prepare("INSERT INTO $tbl_name (usuario, comentario, fecha, estado) VALUES (?, ?, ?, ?)");\n\n    // Bind the parameters\n    $stmt->bind_param("ssss", $usuario, $falla, $fecha, $estado);\n\n    // Set the parameter values\n    $usuario = mysql_real_escape_string(stripslashes($_POST['usuario']));\n    $falla = mysql_real_escape_string(stripslashes($falla));\n    $fecha = mysql_real_escape_string(stripslashes($_POST['fecha']));\n    $estado = '0';\n\n    // Execute the query\n    if (!$stmt->execute()) {\n        echo "Error: " . $stmt->error;\n    }\n\n    // Close the statement and connection\n    $stmt->close();\n    $mysqli->close();\n\n    header("location:../../user/usuario.php");\n}\n?>