function check_email($email) {\n    $host = substr(strrchr($email, "@"), 1);\n    $fp = fsockopen("tcp://$host", 25, $errno, $errstr, 30);\n    if (!$fp) {\n        return false;\n    }\n\n    $out = "HELO $host\r\n";\n    fwrite(STDOUT, $out);\n    $out = "MAIL FROM: <>\r\n";\n    fwrite(STDOUT, $out);\n    $out = "RCPT TO: <$email>\r\n";\n    fwrite(STDOUT, $out);\n    $response = fread(STDOUT, 1024);\n    fclose($STDIN);\n    fclose($STDOUT);\n\n    if (strpos($response, "250") !== false) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\n$email = "example@example.com";\nif (check_email($email)) {\n    echo "Email address exists";\n} else {\n    echo "Email address does not exist or cannot be verified";\n}