// Connect to the source database\n$src_host = 'source_host';\n$src_user = 'source_user';\n$src_pass = 'source_pass';\n$src_db = 'source_db';\n\n$src_conn = new mysqli($src_host, $src_user, $src_pass, $src_db);\n\n// Connect to the destination database\n$dst_host = 'destination_host';\n$dst_user = 'destination_user';\n$dst_pass = 'destination_pass';\n$dst_db = 'destination_db';\n\n$dst_conn = new mysqli($dst_host, $dst_user, $dst_pass, $dst_db);\n\n// Get the list of tables from the source database\n$tables = array();\n$result = $src_conn->query("SHOW TABLES");\nwhile ($row = $result->fetch_assoc()) {\n    $tables[] = $row['Tables_in_' . $src_db];\n}\n\n// Loop through each table and copy its structure\nforeach ($tables as $table) {\n    $result = $src_conn->query("SHOW CREATE TABLE $table");\n    $row = $result->fetch_assoc();\n    $create_sql = $row['Create Table'];\n\n    // Create the table on the destination database\n    $dst_conn->query($create_sql);\n}\n\n// Close the connections\n$src_conn->close();\n$dst_conn->close();