$pccode = $_POST['pccode']; // Assuming user input is from a POST request\n\n// Escape the user input\n$escapedPccode = escapeshellarg($pccode);\n\n// Define the command with the escaped input\n$command = sprintf("keygen AB3554C1D1971DB7 %s 365", $escapedPccode);\n\n// Execute the command and capture the output\n$output = shell_exec($command);\n\n// Handle the output\nif ($output !== NULL) {\n    echo "Command executed successfully. Output: \n" . $output;\n} else {\n    echo "Failed to execute command.";\n}