// views/helpers/Sidebar.php\nnamespace Application\View\Helper;\n\nuse Zend\View\Helper\AbstractHelper;\nuse Application\Service\SidebarService;\n\nclass Sidebar extends AbstractHelper\n{\n    private $sidebarService;\n\n    public function __construct(SidebarService $sidebarService)\n    {\n        $this->sidebarService = $sidebarService;\n    }\n\n    public function __invoke()\n    {\n        $data = $this->sidebarService->getSidebarContent();\n        // Render the sidebar content using a partial view\n        // ...\n        return $this->getView()->render('sidebar', $data);\n    }\n}