function splitArrayAtBlankLines($array) {\n    $result = array();\n    $currentSection = array();\n    foreach ($array as $line) {\n        if (trim($line) == '') {\n            if (!empty($currentSection)) {\n                $result[] = $currentSection;\n                $currentSection = array();\n            }\n        } else {\n            $currentSection[] = $line;\n        }\n    }\n    if (!empty($currentSection)) {\n        $result[] = $currentSection;\n    }\n    return $result;\n}