// Store user's IP address when they log in\n$_SESSION['ip_address'] = $_SERVER['REMOTE_ADDR'];\n\n// ASX file-serving script\nif ($_SERVER['REMOTE_ADDR'] === $_SESSION['ip_address']) {\n    // IP address matches, serve ASX file\n    header('Content-Type: video/x-ms-asf');\n    readfile('path/to/your/asx/file.asx');\n    exit;\n} else {\n    // IP address does not match, deny access\n    header('HTTP/1.1 403 Forbidden');\n    exit;\n}