require_once 'phpthumb.class.php';\n\n// Create a new image object\n$phpThumb = new phpThumb();\n\n// Create a new image from a file\n$image1 = $phpThumb->generateThumbnail('image1.png', 100, 100);\n$image2 = $phpThumb->generateThumbnail('image2.png', 100, 100);\n\n// Create a new image with a transparent background\n$transparentImage = imagecreatetruecolor(100, 100);\nimagefill($transparentImage, 0, 0, imagecolorallocatealpha($transparentImage, 0, 0, 0, 127));\n\n// Copy the first image onto the transparent image\nimagecopy($transparentImage, $image1, 0, 0, 0, 0, 100, 100);\n\n// Copy the second image onto the transparent image, offset by 20 pixels\nimagecopy($transparentImage, $image2, 20, 20, 0, 0, 100, 100);\n\n// Output the merged image as a PNG\nheader('Content-Type: image/png');\nimagepng($transparentImage);