$xml = simplexml_load_string('\n  <favorites>\n    <interesting>\n        <type>Movie</type>\n        <character>James Bond</character>\n        <name>Casino Royale</name>\n    </interesting>\n    <interesting>\n        <type>Movie</type>\n        <character>Jason Bourne</character>\n        <name>Bourne Identity</name>\n    </interesting>\n    <interesting>\n        <type>Book</type>\n        <character>Lindsay Ford</character>\n        <name>Shantaram</name>\n    </interesting>\n  </favorites>\n');\n\n$grouped = array();\nforeach ($xml->interesting as $item) {\n    $type = (string)$item->type;\n    if (!isset($grouped[$type])) {\n        $grouped[$type] = array();\n    }\n    $grouped[$type][] = (string)$item->name . ' - ' . (string)$item->character;\n}\n\nforeach ($grouped as $type => $items) {\n    echo "<strong>$type</strong>";\n    echo "<ul>";\n    foreach ($items as $item) {\n        echo "<li>$item</li>";\n    }\n    echo "</ul>";\n}