// Model\npublic function getLastVisitDate() {\n    $date = $this->db->get('last_visit_date');\n    if ($date === null) {\n        return ['Never', true]; // true indicates display in red\n    } else {\n        return [$date, false];\n    }\n}\n\n// Controller\npublic function displayLocationInfo() {\n    $location = new LocationModel();\n    $lastVisitDate = $location->getLastVisitDate();\n    // ...\n    $this->view->render('location_info', ['lastVisitDate' => $lastVisitDate]);\n}\n\n// View\n<?php if ($lastVisitDate[1]) : ?>\n    <span style="color: red"><?= $lastVisitDate[0] ?></span>\n<?php else : ?>\n    <?= $lastVisitDate[0] ?>\n<?php endif ?>