$array = array();\nfor ($i = 0; $i < 100000; $i++) {\n    $array[] = rand(1, 100);\n}\n\n$start = microtime(true);\n$array_unique = array_unique($array);\n$end = microtime(true);\necho "array_unique took " . ($end - $start) . " seconds\n";\n\n$start = microtime(true);\n$in_array = array();\nforeach ($array as $value) {\n    if (!in_array($value, $in_array)) {\n        $in_array[] = $value;\n    }\n}\n$end = microtime(true);\necho "in_array took " . ($end - $start) . " seconds\n";