$reslt = mysql_query("SELECT fc.name AS cat_name, f.name AS forum_name\n                      FROM forums f\n                      JOIN forumcats fc ON f.cat_id = fc.id\n                      ORDER BY fc.name, f.name");\n\n$curr_cat = '';\nwhile ($row = mysql_fetch_assoc($reslt)) {\n    if ($row['cat_name'] != $curr_cat) {\n        echo "<h1>{$row['cat_name']}</h1>";\n        $curr_cat = $row['cat_name'];\n    }\n    echo "<h3>{$row['forum_name']}</h3>";\n}