function is_search_engine($user_agent) {\n  $search_engines = array(\n    'Googlebot' => 'Google',\n    'Bingbot' => 'Bing',\n    'Slurp' => 'Yahoo',\n    'DuckDuckBot' => 'DuckDuckGo',\n    'YandexBot' => 'Yandex'\n  );\n\n  foreach ($search_engines as $bot => $engine) {\n    if (strpos($user_agent, $bot) !== false) {\n      return $engine;\n    }\n  }\n\n  return false;\n}\n\n$user_agent = $_SERVER['HTTP_USER_AGENT'];\n$search_engine = is_search_engine($user_agent);\n\nif ($search_engine) {\n  // Log the search engine visit\n  $log_file = 'search_engine_visits.log';\n  $log_entry = date('Y-m-d H:i:s') . ' ' . $search_engine . ' ' . $_SERVER['REQUEST_URI'] . "\n";\n  file_put_contents($log_file, $log_entry, FILE_APPEND);\n}