// gallery.php\n\n// set the number of images per page\n$images_per_page = 10;\n\n// get the current page number from the query string\n$page = isset($_GET['page']) ? (int) $_GET['page'] : 1;\n\n// get the total number of images\n$images = scandir('images');\n$total_images = count($images) - 2; // subtract 2 because scandir includes . and ..\n$total_pages = ceil($total_images / $images_per_page);\n\n// calculate the start and end image numbers for the current page\n$start_image = ($page - 1) * $images_per_page;\n$end_image = $start_image + $images_per_page;\n\n// display the images for the current page\nfor ($i = $start_image; $i < $end_image && $i < $total_images; $i++) {\n    echo '<img src="images/' . $images[$i + 2] . '">';\n}\n\n// display the pagination links\nfor ($i = 1; $i <= $total_pages; $i++) {\n    if ($i == $page) {\n        echo 'Page ' . $i . ' ';\n    } else {\n        echo '<a href="gallery.php?page=' . $i . '">Page ' . $i . '</a> ';\n    }\n}