// Define the command to run\n$command = 'pdflatex -output-directory=/path/to/output /path/to/input.tex';\n\n// Use exec() with caution\nexec($command, $output, $return_var);\n\n// Check if the command was successful\nif ($return_var === 0) {\n    // Command was successful, do something with the output\n    print_r($output);\n} else {\n    // Command failed, handle the error\n    error_log("Command failed with return code $return_var");\n}