function get_value($array, $string) {\n    $keys = explode('.', str_replace(array('[', ']', '"'), array('.', '', ''), $string));\n    $value = $array;\n    foreach ($keys as $key) {\n        if (isset($value[$key])) {\n            $value = $value[$key];\n        } else {\n            return null;\n        }\n    }\n    return $value;\n}\n\n$data = array(\n    "status" => "success",\n    "response" => array(\n        "url" => "https://example.com"\n    ),\n    "entry" => array(\n        0 => array(\n            "text" => "Hello World"\n        )\n    )\n);\n\n$string = 'data["status"]';\n$string = str_replace('data', '', $string);\necho get_value($data, $string); // outputs: success\n\n$string = 'data["response"]["url"]';\n$string = str_replace('data', '', $string);\necho get_value($data, $string); // outputs: https://example.com\n\n$string = 'data["entry"]["0"]["text"]';\n$string = str_replace('data', '', $string);\necho get_value($data, $string); // outputs: Hello World